driver.Credentials.Add(new Uri("http://www.chive.com"), "Basic", nc1);

NetworkCredential nc2 = new NetworkCredential("john", "password", "UK");
driver.Credentials.Add(new Uri("http://www.xyz.com"), "Digest", nc2);
