member.example:
    The following example demonstrates how to use the ItemDataBound
    event.

protected void RadRotator1_ItemDataBound(object sender, Telerik.WebControls.RadRotatorItemEventArgs e)
{
    TextBox textBox = (TextBox)e.Item.FindControl("TextBox1");
    textBox.Text = (string)DataBinder.Eval(e.Item.DataItem, "Text");
}


Protected Sub RadRotator1_ItemDataBound(ByVal sender As Object, ByVal e As Telerik.WebControls.RadRotatorItemEventArgs) Handles RadRotator1.ItemDataBound
    Dim textBox As TextBox = e.Item.FindControl("TextBox1")
    textBox.Text = CStr(DataBinder.Eval(e.Item.DataItem, "Text"))
End Sub
