member.example:
    Here is an example code used to create a datasource and bind a
    RadToolbar instance to it. 

DataTable dt = new DataTable();        
dt.Columns.Add("CommandName");
dt.Columns.Add("ToolTip");
dt.Columns.Add("ButtonImage");

DataRow dr = dt.NewRow();
dr["CommandName"] = "new";
dr["ToolTip"] = "New Document";
dr["ButtonImage"] = "new.gif";
dt.Rows.Add(dr);

DataRow dr1 = dt.NewRow();
dr1["CommandName"] = "open";
dr1["ToolTip"] = "Open Document";
dr1["ButtonImage"] = "open.gif";
dt.Rows.Add(dr1);

DataRow dr2 = dt.NewRow();
dr2["CommandName"] = "save";
dr2["ToolTip"] = "Save Document";
dr2["ButtonImage"] = "save.gif";
dt.Rows.Add(dr2);

toolbar1.DataSource = dt;
toolbar1.DataBind();


Dim dt As New DataTable
dt.Columns.Add("CommandName")
dt.Columns.Add("ToolTip")
dt.Columns.Add("ButtonImage")

Dim dr As DataRow = dt.NewRow()
dr("CommandName") = "new"
dr("ToolTip") = "New Document"
dr("ButtonImage") = "new.gif"
dt.Rows.Add(dr)

